import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { Validators } from '@angular/forms';
let ContactComponent = class ContactComponent {
    constructor(contactoService, formBuilder) {
        this.contactoService = contactoService;
        this.formBuilder = formBuilder;
        this.error = true;
        this.siteKey = "6LfFCdsgAAAAABIOTCS94LPa0_H8OuGf4QvJnzGc";
        this.contacto = {
            nombre: "",
            apellidos: "",
            documento: "DNI",
            numero_documento: "",
            correo: "",
            telefono: "",
            mensaje: ""
        };
        this.flagRespuesta = false;
        this.flagCheck = true;
        this.flagDataPersonal = false;
    }
    ngOnInit() {
        this.aFormGroup = this.formBuilder.group({
            recaptcha: ['', Validators.required]
        });
    }
    validar() {
        this.error = false;
        console.log(this.error);
    }
    onSubmit() {
        this.contactoService.create(this.contacto).subscribe(data => {
            this.enviarContacto(true);
        }, error => {
            this.enviarContacto(false);
        });
    }
    async enviarContacto(check) {
        if (check) {
            this.contacto.nombre = "";
            this.contacto.apellidos = "";
            this.contacto.documento = "DNI";
            this.contacto.numero_documento = "";
            this.contacto.telefono = "";
            this.contacto.mensaje = "";
            this.contacto.correo = "";
        }
        this.flagCheck = check;
        this.flagRespuesta = true;
        await new Promise(f => setTimeout(f, 2000));
        this.flagRespuesta = false;
    }
};
ContactComponent = __decorate([
    Component({
        selector: 'app-contact',
        templateUrl: './contact.component.html',
        styleUrls: ['./contact.component.css']
    })
], ContactComponent);
export { ContactComponent };
